const staticData = getWorkflowStaticData('global');
const newItem = items.map(item => item.json["ip"]);
const ildItem = staticData.ildItem; 

if (!ildItem) {
  staticData.ildItem = newItem;
  return items;
}


const actualnewItem = newItem.filter((id) => !ildItem.includes(id));
const actualItem = items.filter((data) => actualnewItem.includes(data.json['ip']));
staticData.ildItem = [...actualnewItem, ...ildItem];

return actualItem;
